mapper.select:
select t_hs.HOUSE_ID,t_hs.HOUSE_NAME,t_hs.HOUSE_ESTATE,t_hs.HOUSE_LOCATION_PROVINCE,
t_hs.HOUSE_LOCATION_CITY,t_hs.HOUSE_LOCATION_DISTRICT,t_hs.HOUSE_LOCATION_STREET,t_hs.HOUSE_PRICE,
t_hs.HOUSE_SQUARE,t_hs.HOUSE_STATE,t_hs.HOUSE_CLIENT_ID,t_hs.COMPLETE_TIME,t_hs.DEAL_TIME,
t_cs.CLIENT_STAFF_ID,t_cs.CLIENT_NAME,t_cs.CLIENT_PHONE,t_cs.CLIENT_SEX,
t_hs.HOUSE_PURCHASER_ID,t_wb.CLIENT_NAME AS W_CLIENT_NAME,t_wb.CLIENT_PHONE AS W_CLIENT_PHONE,
t_wb.CLIENT_SEX AS W_CLIENT_SEX
from t_house_sale t_hs INNER JOIN t_client_sale t_cs
on t_hs.HOUSE_CLIENT_ID = t_cs.CLIENT_ID
left join t_client_want_buy t_wb
on t_hs.HOUSE_PURCHASER_ID=t_wb.CLIENT_ID
where
1=1
and t_hs.STATUS=1
and t_cs.CLIENT_STAFF_ID=#{criteria.clientStaffId}
and t_cs.STATUS=1
and t_hs.HOUSE_ID = #{criteria.houseId,jdbcType=BIGINT}


    and t_hs.HOUSE_NAME like #{houseName}



    and t_hs.HOUSE_ESTATE like #{houseEstate}

and t_hs.HOUSE_LOCATION_PROVINCE = #{criteria.houseLocationProvince,jdbcType=VARCHAR}
and t_hs.HOUSE_LOCATION_CITY = #{criteria.houseLocationCity,jdbcType=VARCHAR}
and t_hs.HOUSE_LOCATION_DISTRICT = #{criteria.houseLocationDistrict,jdbcType=VARCHAR}
and t_hs.HOUSE_LOCATION_STREET = #{criteria.houseLocationStreet,jdbcType=VARCHAR}

and t_hs.HOUSE_PRICE between #{criteria.startHousePrice,jdbcType=INTEGER} and #{criteria.endHousePrice,jdbcType=INTEGER}


and t_hs.HOUSE_SQUARE between #{criteria.startHouseSquare,jdbcType=INTEGER} and #{criteria.endHouseSquare,jdbcType=INTEGER}


    and HOUSE_STATE = #{criteria.houseState,jdbcType=INTEGER}

and HOUSE_CLIENT_ID = #{criteria.houseClientId,jdbcType=BIGINT}
and COMPLETE_TIME = #{criteria.completeTime,jdbcType=VARCHAR}

and t_hs.DEAL_TIME between #{criteria.startDealTime,jdbcType=TIMESTAMP} and #{criteria.endDealTime,jdbcType=TIMESTAMP}

and HOUSE_PURCHASER_ID = #{criteria.housePurachaserId,jdbcType=INTEGER}


    and t_cs.CLIENT_NAME like #{clientName}

and t_cs.CLIENT_SEX = #{criteria.clientSex,jdbcType=VARCHAR}


    and t_cs.CLIENT_PHONE like #{clientPhone}



    and t_wb.CLIENT_NAME like #{wClientName}

and t_wb.CLIENT_SEX = #{criteria.wClientSex,jdbcType=VARCHAR}


    and t_wb.CLIENT_PHONE like #{wClientPhone}

limit #{pagination.offset}, #{pagination.limit}

select COUNT(1)
from t_house_sale t_hs INNER JOIN t_client_sale t_cs
on t_hs.HOUSE_CLIENT_ID = t_cs.CLIENT_ID
left join t_client_want_buy t_wb
on t_hs.HOUSE_PURCHASER_ID=t_wb.CLIENT_ID
where
1=1
and t_hs.STATUS=1
and t_cs.CLIENT_STAFF_ID=#{criteria.clientStaffId}
and t_cs.STATUS=1
and t_hs.HOUSE_ID = #{criteria.houseId,jdbcType=BIGINT}


    and t_hs.HOUSE_NAME like #{houseName}



    and t_hs.HOUSE_ESTATE like #{houseEstate}

and t_hs.HOUSE_LOCATION_PROVINCE = #{criteria.houseLocationProvince,jdbcType=VARCHAR}
and t_hs.HOUSE_LOCATION_CITY = #{criteria.houseLocationCity,jdbcType=VARCHAR}
and t_hs.HOUSE_LOCATION_DISTRICT = #{criteria.houseLocationDistrict,jdbcType=VARCHAR}
and t_hs.HOUSE_LOCATION_STREET = #{criteria.houseLocationStreet,jdbcType=VARCHAR}

    and t_hs.HOUSE_PRICE between #{criteria.startHousePrice,jdbcType=INTEGER} and #{criteria.endHousePrice,jdbcType=INTEGER}


    and t_hs.HOUSE_SQUARE between #{criteria.startHouseSquare,jdbcType=INTEGER} and #{criteria.endHouseSquare,jdbcType=INTEGER}


    and HOUSE_STATE = #{criteria.houseState,jdbcType=INTEGER}

and HOUSE_CLIENT_ID = #{criteria.houseClientId,jdbcType=BIGINT}
and COMPLETE_TIME = #{criteria.completeTime,jdbcType=VARCHAR}

    and t_hs.DEAL_TIME between #{criteria.startDealTime,jdbcType=TIMESTAMP} and #{criteria.endDealTime,jdbcType=TIMESTAMP}

and HOUSE_PURCHASER_ID = #{criteria.housePurachaserId,jdbcType=INTEGER}


    and t_cs.CLIENT_NAME like #{clientName}

and t_cs.CLIENT_SEX = #{criteria.clientSex,jdbcType=VARCHAR}


    and t_cs.CLIENT_PHONE like #{clientPhone}



    and t_wb.CLIENT_NAME like #{wClientName}

and t_wb.CLIENT_SEX = #{criteria.wClientSex,jdbcType=VARCHAR}


    and t_wb.CLIENT_PHONE like #{wClientPhone}

select t_hs.HOUSE_ID,t_hs.HOUSE_NAME,t_hs.HOUSE_ESTATE,t_hs.HOUSE_LOCATION_PROVINCE,
